import React from 'react';
import { calculateAverage } from '../../services/apiService';

const Reports = ({ reports, students }) => {
  return (
    <div className="reports-section">
      <h2>üìà Relat√≥rios e Estat√≠sticas</h2>

      <div className="report-card">
        <h3>üìö M√©dias por Disciplina:</h3>
        {reports.classAverageBySubject.map((avg, index) => (
          <div key={index} className="subject-average">
            <strong>Disciplina {index + 1}:</strong> {avg}
          </div>
        ))}
        <div className="overall-average">
          <strong>M√©dia Geral da Turma: {reports.overallClassAverage}</strong>
        </div>
      </div>

      <div className="report-card">
        <h3>‚≠ê Alunos Acima da M√©dia da Turma:</h3>
        {reports.studentsAboveAverage.length > 0 ? (
          reports.studentsAboveAverage.map(student => (
            <div key={student.id} className="student-highlight">
              <strong>{student.name}</strong> - M√©dia: {calculateAverage(student.grades)}
            </div>
          ))
        ) : (
          <p className="no-data">Nenhum aluno acima da m√©dia da turma</p>
        )}
      </div>

      <div className="report-card alert">
        <h3>‚ö†Ô∏è Alunos com Frequ√™ncia Abaixo de 75%:</h3>
        {reports.studentsBelowAttendance.length > 0 ? (
          reports.studentsBelowAttendance.map(student => (
            <div key={student.id} className="student-warning">
              <strong>{student.name}</strong> - Frequ√™ncia: {student.attendance}%
            </div>
          ))
        ) : (
          <p className="no-data">Todos os alunos com frequ√™ncia adequada</p>
        )}
      </div>
    </div>
  );
};

export default Reports;
