// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import './App.css';

const API_URL = 'http://localhost:3001/api';

function App() {
  const [students, setStudents] = useState([]);
  const [reports, setReports] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [editingStudent, setEditingStudent] = useState(null);

  const [newStudent, setNewStudent] = useState({
    name: '',
    grades: ['', '', '', 0, 0],
    attendance: ''
  });

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${API_URL}/students`);
      const data = await response.json();
      setStudents(data);
    } catch (error) {
      console.error('Erro ao buscar alunos:', error);
      alert('Erro de conexão. Verifique se o backend está rodando.');
    } finally {
      setLoading(false);
    }
  };

  const fetchReports = async () => {
    try {
      const response = await fetch(`${API_URL}/reports`);
      const data = await response.json();
      setReports(data);
    } catch (error) {
      console.error('Erro ao buscar relatórios:', error);
    }
  };

  const handleAddStudent = async (e) => {
    e.preventDefault();

    if (!newStudent.name.trim()) {
      alert('Nome é obrigatório');
      return;
    }

    if (newStudent.grades.some(g => g === '' || isNaN(g)) || newStudent.attendance === '') {
      alert('Preencha todas as notas e frequência');
      return;
    }

    try {
      setLoading(true);
      const response = await fetch(`${API_URL}/students`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newStudent)
      });

      if (response.ok) {
        await fetchStudents();
        setNewStudent({ name: '', grades: [0, 0, 0, 0, 0], attendance: '' });
        alert('Aluno adicionado!');
      } else {
        const error = await response.json();
        alert(`Erro: ${error.error}`);
      }
    } catch (error) {
      alert('Erro de conexão');
    } finally {
      setLoading(false);
    }
  };

  const handleEditStudent = async (e) => {
    e.preventDefault();

    if (!editingStudent.name.trim()) {
      alert('Nome é obrigatório');
      return;
    }

    if (editingStudent.grades.some(g => g === '' || isNaN(g)) || editingStudent.attendance === '') {
      alert('Preencha todas as notas e frequência');
      return;
    }

    try {
      setLoading(true);
      const response = await fetch(`${API_URL}/students/${editingStudent.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(editingStudent)
      });

      if (response.ok) {
        await fetchStudents();
        setEditingStudent(null);
        alert('Aluno atualizado!');
      } else {
        const error = await response.json();
        alert(`Erro: ${error.error}`);
      }
    } catch (error) {
      alert('Erro de conexão');
    } finally {
      setLoading(false);
    }
  };

  const startEditing = (student) => {
    setEditingStudent({
      id: student.id,
      name: student.name,
      grades: [...student.grades],
      attendance: student.attendance
    });
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleRemoveStudent = async (id) => {
    if (!window.confirm('Remover este aluno?')) return;

    try {
      setLoading(true);
      const response = await fetch(`${API_URL}/students/${id}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        await fetchStudents();
        alert('Aluno removido!');
      }
    } catch (error) {
      alert('Erro ao remover aluno');
    } finally {
      setLoading(false);
    }
  };

  const toggleResults = async () => {
    if (!showResults) {
      await fetchReports();
    }
    setShowResults(!showResults);
  };

  const calculateAverage = (grades) => {
    const sum = grades.reduce((acc, grade) => acc + parseFloat(grade), 0);
    return (sum / grades.length).toFixed(2);
  };

  return (
    <div className="app">
      <header className="header">
        <h1>Sistema de Gerenciamento de Alunos</h1>
        <p>Professor Carlos - Organize notas e frequência</p>
      </header>

      {/* Formulário de Edição (aparece somente quando editingStudent não é null) */}
      {editingStudent && (
        <section className="form-section edit-mode">
          <h2>✏️ Editar Aluno</h2>
          <form onSubmit={handleEditStudent} className="student-form">
            <div className="form-group">
              <label>Nome do Aluno:</label>
              <input
                type="text"
                value={editingStudent.name}
                onChange={(e) => setEditingStudent({...editingStudent, name: e.target.value})}
                placeholder="Digite o nome"
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Notas das 5 Disciplinas (0-10):</label>
              <div className="disciplinas-container">
                {editingStudent.grades.map((grade, index) => (
                  <div key={index} className="disciplina-input">
                    <label>Disciplina {index + 1}:</label>
                    <input
                      type="number"
                      min="0"
                      max="10"
                      step="0.1"
                      value={grade}
                      onChange={(e) => {
                        const newGrades = [...editingStudent.grades];
                        newGrades[index] = e.target.value;
                        setEditingStudent({...editingStudent, grades: newGrades});
                      }}
                      disabled={loading}
                    />
                  </div>
                ))}
              </div>
            </div>

            <div className="form-group">
              <label>Frequência (0-100%):</label>
              <input
                type="number"
                min="0"
                max="100"
                value={editingStudent.attendance}
                onChange={(e) => setEditingStudent({...editingStudent, attendance: e.target.value})}
                placeholder="Ex: 85"
                disabled={loading}
                className="attendance-input"
              />
            </div>

            <div className="edit-buttons">
              <button type="submit" disabled={loading} className="btn-primary">
                {loading ? 'Atualizando...' : '✔️ Salvar Alterações'}
              </button>
              <button 
                type="button"
                onClick={() => setEditingStudent(null)}
                disabled={loading}
                className="btn-secondary"
              >
                Cancelar
              </button>
            </div>
          </form>
        </section>
      )}

      {/* Formulário de Adicionar Novo Aluno (aparece somente quando não está editando) */}
      {!editingStudent && (
        <section className="form-section">
          <h2>📝 Adicionar Novo Aluno</h2>
          <form onSubmit={handleAddStudent} className="student-form">
            <div className="form-group">
              <label>Nome do Aluno:</label>
              <input
                type="text"
                value={newStudent.name}
                onChange={(e) => setNewStudent({...newStudent, name: e.target.value})}
                placeholder="Digite o nome"
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Notas das 5 Disciplinas (0-10):</label>
              <div className="disciplinas-container">
                {newStudent.grades.map((grade, index) => (
                  <div key={index} className="disciplina-input">
                    <label>Disciplina {index + 1}:</label>
                    <input
                      type="number"
                      min="0"
                      max="10"
                      step="0.1"
                      value={grade}
                      onChange={(e) => {
                        const newGrades = [...newStudent.grades];
                        newGrades[index] = e.target.value;
                        setNewStudent({...newStudent, grades: newGrades});
                      }}
                      disabled={loading}
                    />
                  </div>
                ))}
              </div>
            </div>

            <div className="form-group">
              <label>Frequência (0-100%):</label>
              <input
                type="number"
                min="0"
                max="100"
                value={newStudent.attendance}
                onChange={(e) => setNewStudent({...newStudent, attendance: e.target.value})}
                placeholder="Ex: 85"
                disabled={loading}
                className="attendance-input"
              />
            </div>

            <button type="submit" disabled={loading} className="btn-primary">
              {loading ? 'Adicionando...' : '➕ Adicionar Aluno'}
            </button>
          </form>
        </section>
      )}

      {/* Lista de Alunos */}
      {students.length > 0 && (
        <section className="students-section">
          <h2>👥 Alunos Cadastrados ({students.length})</h2>
          <div className="table-container">
            <table className="students-table">
              <thead>
                <tr>
                  <th>Nome</th>
                  <th>Disciplina 1</th>
                  <th>Disciplina 2</th>
                  <th>Disciplina 3</th>
                  <th>Disciplina 4</th>
                  <th>Disciplina 5</th>
                  <th>Média</th>
                  <th>Frequência</th>
                  <th>Ações</th>
                </tr>
              </thead>
              <tbody>
                {students.map(student => (
                  <tr key={student.id}>
                    <td><strong>{student.name}</strong></td>
                    {student.grades.map((grade, index) => (
                      <td key={index}>{parseFloat(grade).toFixed(1)}</td>
                    ))}
                    <td className={calculateAverage(student.grades) >= 6 ? 'grade-good' : 'grade-bad'}>
                      <strong>{calculateAverage(student.grades)}</strong>
                    </td>
                    <td className={student.attendance >= 75 ? 'attendance-good' : 'attendance-bad'}>
                      {student.attendance}%
                    </td>
                    <td>
                      <button 
                        onClick={() => startEditing(student)}
                        className="btn-edit"
                        disabled={loading}
                      >
                        Editar
                      </button>
                      <button 
                        onClick={() => handleRemoveStudent(student.id)}
                        className="btn-danger"
                        disabled={loading}
                      >
                        Deletar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <button 
            onClick={toggleResults}
            disabled={loading}
            className="btn-secondary"
          >
            📊 {showResults ? 'Ocultar' : 'Mostrar'} Relatórios
          </button>
        </section>
      )}

      {/* Relatórios */}
      {showResults && reports && (
        <section className="reports-section">
          <h2>📈 Relatórios e Estatísticas</h2>

          <div className="report-card">
            <h3>📚 Médias por Disciplina:</h3>
            {reports.classAverageBySubject.map((avg, index) => (
              <div key={index} className="subject-average">
                <strong>Disciplina {index + 1}:</strong> {avg}
              </div>
            ))}
            <div className="overall-average">
              <strong>Média Geral da Turma: {reports.overallClassAverage}</strong>
            </div>
          </div>

          <div className="report-card">
            <h3>⭐ Alunos Acima da Média da Turma:</h3>
            {reports.studentsAboveAverage.length > 0 ? (
              reports.studentsAboveAverage.map(student => (
                <div key={student.id} className="student-highlight">
                  <strong>{student.name}</strong> - Média: {calculateAverage(student.grades)}
                </div>
              ))
            ) : (
              <p className="no-data">Nenhum aluno acima da média da turma</p>
            )}
          </div>

          <div className="report-card alert">
            <h3>⚠️ Alunos com Frequência Abaixo de 75%:</h3>
            {reports.studentsBelowAttendance.length > 0 ? (
              reports.studentsBelowAttendance.map(student => (
                <div key={student.id} className="student-warning">
                  <strong>{student.name}</strong> - Frequência: {student.attendance}%
                </div>
              ))
            ) : (
              <p className="no-data">Todos os alunos com frequência adequada</p>
            )}
          </div>
        </section>
      )}

      {/* Estado vazio */}
      {students.length === 0 && !loading && (
        <section className="empty-state">
          <h3>👋 Bem-vindo!</h3>
          <p>Comece adicionando alguns alunos para ver os relatórios.</p>
        </section>
      )}

      {/* Loading */}
      {loading && (
        <div className="loading">
          <p>Carregando...</p>
        </div>
      )}
    </div>
  );
}

export default App;
